import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery } from '@apollo/client';
import { Helmet } from "react-helmet";
import { QUERY_SINGLE_POST } from '../utils/queries';

const SinglePost = () => {
  const { postId } = useParams();
  const { loading, data } = useQuery(QUERY_SINGLE_POST, {
    variables: { postId: postId },
  });
  const post = data?.post || {};

  const [hoveredIndex, setHoveredIndex] = useState(-1);
  const [showPrompt, setShowPrompt] = useState(false);
  const [promptPos, setPromptPos] = useState({ x: 0, y: 0 });

  if (loading) {
    return (
      <div className="spinner">
        <div className="spinner1"></div>
      </div>
    );
  }

  const sentences = post.postText.split(/(?<=\.|\?|\!)\s/g);

  return (
    <div className="appear single-post">
      <Helmet>
        <title>Plot Weave | {post.postTitle}</title>
      </Helmet>
      <div className="my-3">
        <h3 className="created-by right">
          {post.postTitle}
          <br />
          <span style={{ fontSize: '1rem' }}>{post.createdAt}</span>
        </h3>
        <div className="post-container">
          {sentences.map((sentence, index) => {
            const isHovered = index === hoveredIndex;
            return (
              <span
                key={index}
                style={{ display: 'inline-block', position: 'relative' }}
                onMouseEnter={(event) => {
                  setHoveredIndex(index);
                  setShowPrompt(true);
                  setPromptPos({ x: event.clientX, y: event.clientY });
                }}
                onMouseLeave={() => {
                  setHoveredIndex(-1);
                  setShowPrompt(false);
                }}
              >
                <a href={`/weave/${postId}?lastSentenceIndex=${index}`}>
                  {sentence}
                </a>
                {isHovered && showPrompt && (
                  <div
                    style={{
                      position: 'absolute',
                      top: promptPos.y - 40,
                      left: promptPos.x,
                      backgroundColor: '#fff',
                      padding: '0.25rem',
                      borderRadius: '0.25rem',
                      boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.25)',
                    }}
                  >
                    Weave
                  </div>
                )}
              </span>
            );
          })}
        </div>
      </div>
      <div className="cont-space"></div>
    </div>
  );
};

export default SinglePost;
