import React, { useState, useEffect } from "react";
import { useMutation } from "@apollo/client";
import { useLocation } from "react-router-dom";
import { ADD_WEAVE } from "../utils/mutations";

const Weave = () => {
  const [postText, setPostText] = useState("");
  const [weaveTitle, setWeaveTitle] = useState("");
  const [addWeave] = useMutation(ADD_WEAVE);

  const location = useLocation();
  const sentenceIndex = new URLSearchParams(location.search).get(
    "sentenceIndex"
  );
  const sentenceText = new URLSearchParams(location.search).get(
    "sentenceText"
  );

  useEffect(() => {
    setPostText(sentenceText);
  }, [sentenceText]);

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    try {
      const { data } = await addWeave({
        variables: {
          weaveTitle,
          postText,
        },
      });
      setWeaveTitle("");
      setPostText("");
    } catch (err) {
      console.error(err);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;

    if (name === "postText" && value.length <= 3000) {
      setPostText(value);
    }

    if (name === "weaveTitle" && value.length <= 80) {
      setWeaveTitle(value);
    }
  };

  return (
    <div>
      <h2>Weave</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label htmlFor="weaveTitle">Title:</label>
          <input
            type="text"
            name="weaveTitle"
            value={weaveTitle}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="postText">Post:</label>
          <textarea
            name="postText"
            value={postText}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Weave;
