import React, { useState, useEffect } from 'react';
// import { Link } from 'react-router-dom';
import { useMutation } from '@apollo/client';

import { ADD_POST, ADD_WEAVE } from '../utils/mutations';
import { QUERY_POSTS, QUERY_WEAVES } from '../utils/queries';

import Auth from '../utils/auth';
import NotAuthorized from '../Components/NotAuthorized';
import { Helmet } from "react-helmet";

const Weave = ({ match, selectedText }) => {
  console.log(match.params.postId, match.params.url);
  const [postText, setPostText] = useState(selectedText);
  const [postTitle, setPostTitle] = useState('');
  const [weaveTitle, setWeaveTitle] =useState('');

  const [characterCount, setCharacterCount] = useState(0);

  const [addPost, { error }] = useMutation(ADD_POST, {
    update(cache, { data: { addPost } }) {
      try {
        const posts  = cache.readQuery({ query: QUERY_POSTS });

        cache.writeQuery({
          query: QUERY_POSTS,
          data: { posts: [addPost, posts] },
        });
      } catch (e) {
        console.error(e);
      }
    },
  });

  const [addWeave] = useMutation(ADD_WEAVE, {
    update(cache, { data: { addWeave } }) {
      try {
        const weaves  = cache.readQuery({ query: QUERY_WEAVES });

        cache.writeQuery({
          query: QUERY_WEAVES,
          data: { weaves: [addWeave, weaves] },
        });
      } catch (e) {
        console.error(e);
      }
    },
  });

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    try {
      const { data } = await addWeave({
        variables: {
          weaveTitle,
          postText,
          weaveAuthor: Auth.getProfile().data.username,
        },
      });
      setWeaveTitle('')
      setPostText('');
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    setPostText(selectedText);
  }, [selectedText]);

  const handleChange = (event) => {
    const { name, value } = event.target;

    if (name === 'postText' && value.length <= 3000) {
      setPostText(value);
      setCharacterCount(value.length);
    }

    if (name === 'weaveTitle' && value.length <= 80) {
      setPostTitle('');
    }
  };


  return (
    <div>
      <h1>Weave</h1>
      <p>Post ID: {postId}</p>
      <p>Sentence Index: {sentenceIndex}</p>
    </div>
  );
};

export default Weave;
