import { useState } from 'react';
import { SentenceContext } from './Context';

const ClickedData = ({ children }) => {
  const [clickedSentence, setClickedSentence] = useState(null);

  return (
    <SentenceContext.Provider value={{ clickedSentence, setClickedSentence }}>
      {children}
    </SentenceContext.Provider>
  );
};

export default ClickedData;
