import React, { useState, useEffect } from "react";
import { useMutation } from "@apollo/client";
import { useLocation } from "react-router-dom";
import { ADD_WEAVE } from "../utils/mutations";

const Weave = () => {
  const { search } = useLocation();
  const queryParams = new URLSearchParams(search);
  const sentenceIndex = queryParams.get("sentenceIndex");
  const sentenceText = queryParams.get("sentenceText");

  const [postText, setPostText] = useState("");

  useEffect(() => {
    if (sentenceText) {
      setPostText(sentenceText);
    }
  }, [sentenceText]);

  const [weaveTitle, setWeaveTitle] = useState("");

  const [addWeave, { error }] = useMutation(ADD_WEAVE);

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    try {
      const { data } = await addWeave({
        variables: {
          weaveTitle,
          postText,
        },
      });
      setWeaveTitle("");
      setPostText("");
    } catch (err) {
      console.error(err);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;

    if (name === "postText" && value.length <= 3000) {
      setPostText(value);
    }

    if (name === "weaveTitle" && value.length <= 80) {
      setWeaveTitle(value);
    }
  };

  return (
    <div>
      <h2>Weave</h2>
      <form
          id="spacing"
            className="flex-row justify-center justify-space-between-md align-center"
            onSubmit={handleFormSubmit}
          >
      <div className="post-title">
          <label htmlFor="weaveTitle">Title:</label>
          <input
            type="text"
            name="weaveTitle"
            value={weaveTitle}
            className="title-input"
            onChange={handleChange}
          />
        </div>
        <div className="question-post">
          <label htmlFor="postText">Post:</label>
          <textarea
            name="postText"
            value={postText}
            className="post-input"
            onChange={handleChange}
          />
        </div>
        <button className="start" type="submit">
                Create Post !
              </button>
      </form>
    </div>
  );
};

export default Weave;
