import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery } from '@apollo/client';
import { Helmet } from "react-helmet";
import { QUERY_SINGLE_POST } from '../utils/queries';

const SinglePost = () => {
  const { postId } = useParams();
  const { loading, data } = useQuery(QUERY_SINGLE_POST, {
    variables: { postId: postId },
  });

  const post = data?.post || {};
  const postTextLines = post.postText.split('\n');

  const [hoveredLine, setHoveredLine] = useState(null);

  const handleWeave = (lineIndex) => {
    // Implement your weaving logic here
    alert(`Weaving line ${lineIndex}`);
  };

  if (loading) {
    return (
      <div className="spinner">
        <div className="spinner1"></div>
      </div>
    );
  }

  return (
    <div className="appear single-post">
      <Helmet>
        <title>Plot Weave | {post.postTitle}</title>
      </Helmet>
      <div className="my-3">
        <h3 className="created-by right">
          {post.postTitle}
          <br />
          <span style={{ fontSize: '1rem' }}>{post.createdAt}</span>
        </h3>
        <div className="post-container">
          {postTextLines.map((line, index) => (
            <span key={index} style={{ display: 'block' }}>
              <a
                href="#"
                onMouseEnter={() => setHoveredLine(index)}
                onMouseLeave={() => setHoveredLine(null)}
                onClick={() => handleWeave(index)}
              >
                {line}
              </a>
              {hoveredLine === index && (
                <span
                  style={{
                    position: 'absolute',
                    top: '50%',
                    right: 0,
                    transform: 'translateY(-50%)',
                    backgroundColor: '#fff',
                    padding: '0.25rem',
                  }}
                >
                  <button onClick={() => handleWeave(index)}>Weave</button>
                </span>
              )}
            </span>
          ))}
        </div>
      </div>
      <div className="cont-space"></div>
    </div>
  );
};

export default SinglePost;
