import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery } from '@apollo/client';
import { Helmet } from 'react-helmet';
import { QUERY_SINGLE_POST } from '../utils/queries';
import { Link } from 'react-router-dom';

const SinglePost = () => {
  const { postId } = useParams();

  const { loading, data } = useQuery(QUERY_SINGLE_POST, {
    variables: { postId: postId },
  });

  const post = data?.post || {};

  const [hoveredSentence, setHoveredSentence] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  if (loading) {
    return (
      <div className="spinner">
        <div className="spinner1"></div>
      </div>
    );
  }

  const sentences = post.postText.split('. ');

  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  return (
    <div className="appear single-post" onMouseMove={handleMouseMove}>
      <Helmet>
        <title>Plot Weave | {post.postTitle}</title>
      </Helmet>
      <div className="my-3">
        <h3 className="created-by right">
          {post.postTitle}
          <br />
          <span style={{ fontSize: '1rem' }}>{post.createdAt}</span>
        </h3>
        <div className="post-container">
          {sentences.map((sentence, index) => (
            <span key={index} style={{ display: 'inline-block' }}>
              <Link
                to={`/weave/${postId}/${index}`}
                onMouseEnter={() => setHoveredSentence(index)}
                onMouseLeave={() => setHoveredSentence(null)}
              >
                {sentence}.&nbsp;
              </Link>
              {hoveredSentence === index && (
                <div
                  className="weave-prompt"
                  style={{ top: cursorPosition.y + 10, left: cursorPosition.x + 10 }}
                >
                  <Link to={`/weave/${postId}/${index}`}>Weave</Link>
                </div>
              )}
            </span>
          ))}
        </div>
      </div>
      <div className="cont-space"></div>
    </div>
  );
};

export default SinglePost;
